        // TODO add your handling code here:
        String saida = "Dados dos produtos da Loja\n";
        
        Livro l = new Livro("A Odisseia", "Homero", "BREscola", "Odysseus Editora", 1997, 666, 89);
        saida+= "\n" + l.getDescricaoCompleta();
        saida+= "\n" + l.getPrecoDeVenda();
        saida+= "\n" + l.getTipoProduto();
                
        CD cdzin = new CD("666", "AC/DC", "UmCaraAi", "RockPesad√≠ssimo", 222, 88, "cd");
        saida+= "\n" + cdzin.getDescricaoCompleta();
        saida+= "\n" + cdzin.getPrecoDeVenda();
        saida+= "\n" + cdzin.getTipoProduto();
        
        Notebook acer = new Notebook("Acer", "SP000", 64, 2000, "I5", "LINUX", 24, 777, 6566, "Notebook");
        saida+= "\n" + acer.getDescricaoCompleta();
        saida+= "\n" + acer.getPrecoDeVenda();
        saida+= "\n" + acer.getTipoProduto();
        
        TV lg = new TV("KG--3", "LG", "LINUX", 90, 888, 2555, "TV");
        saida+= "\n" + lg.getDescricaoCompleta();
        saida+= "\n" + lg.getPrecoDeVenda();
        saida+= "\n" + lg.getTipoProduto();
        
        jTextArea1.setText(saida);
